function addHeaderToCodeBlocks(){const e=document.querySelectorAll("pre");e.forEach(e=>{const n=document.createElement("div");n.className="code-header";const s=document.createElement("span");s.className="header-text";const o=e.className.replace("language-","");s.textContent=`swift`;const t=document.createElement("button");t.classList.add("copy-code-button"),t.innerHTML='<i class="far fa-copy"></i>',t.addEventListener("click",()=>{const n=e.querySelector("code").innerText;navigator.clipboard.writeText(n),t.innerHTML='<i class="fas fa-check"></i>',setTimeout(()=>{t.innerHTML='<i class="far fa-copy"></i>'},1500)}),n.appendChild(s),n.appendChild(t),e.parentNode.insertBefore(n,e)})}document.addEventListener("DOMContentLoaded",addHeaderToCodeBlocks);function addPostInfo(){if(window.location.pathname.startsWith("/blog/")){const e=document.querySelectorAll(".post__content h1");e.forEach(e=>{const t=document.createElement("div");t.classList.add("post-info");const s=document.createElement("img");s.src="/images/profileImage.jpeg",s.alt="Author Image";const o=document.createElement("div");o.classList.add("author"),o.textContent="Silviu Vranau";const i=document.createElement("div");i.classList.add("spacer");const n=document.createElement("div");n.innerHTML=n.classList.add("date"),n.textContent=(new Date).toLocaleDateString(),t.appendChild(s),t.appendChild(o),t.appendChild(i),t.appendChild(n);const a=document.createElement("hr");a.classList.add("post-divider"),e.insertAdjacentElement("afterend",t),t.insertAdjacentElement("afterend",a)})}}document.addEventListener("DOMContentLoaded",addPostInfo)