function addHeaderToCodeBlocks(){const e=document.querySelectorAll("pre");e.forEach(e=>{const n=document.createElement("div");n.className="code-header";const s=document.createElement("span");s.className="header-text";const o=e.className.replace("language-","");s.textContent=`swift`;const t=document.createElement("button");t.classList.add("copy-code-button"),t.innerHTML='<i class="far fa-copy"></i>',t.addEventListener("click",()=>{const n=e.querySelector("code").innerText;navigator.clipboard.writeText(n),t.innerHTML='<i class="fas fa-check"></i>',setTimeout(()=>{t.innerHTML='<i class="far fa-copy"></i>'},1500)}),n.appendChild(s),n.appendChild(t),e.parentNode.insertBefore(n,e)})}document.addEventListener("DOMContentLoaded",addHeaderToCodeBlocks)