function addCopyButtonToCodeBlocks(){const e=document.querySelectorAll('code[class^="language-"]');e.forEach(e=>{const t=document.createElement("button");t.classList.add("copy-code-button"),t.innerHTML='<i class="far fa-copy"></i>',t.addEventListener("click",()=>{const n=e.innerText;navigator.clipboard.writeText(n),t.innerHTML='<i class="fas fa-check"></i>',setTimeout(()=>{t.innerHTML='<i class="far fa-copy"></i>'},1500)}),e.parentNode.insertBefore(t,e)})}