document.addEventListener("DOMContentLoaded",()=>{addCopyButtonToCodeBlocks()});function addCopyButtonToCodeBlocks(){const e=document.querySelectorAll('pre > code[class^="language-"]');e.forEach(e=>{const n=document.createElement("div");n.classList.add("code-block-wrapper");const s=document.createElement("div");s.classList.add("code-block-header");const o=document.createElement("div");o.classList.add("code-block-content");const t=document.createElement("button");t.classList.add("copy-code-button"),t.innerHTML='<i class="far fa-copy"></i>',t.addEventListener("click",()=>{const n=e.innerText;navigator.clipboard.writeText(n).then(()=>{t.innerHTML='<i class="fas fa-check"></i>',setTimeout(()=>{t.innerHTML='<i class="far fa-copy"></i>'},1500)}).catch(e=>{console.error("Failed to copy: ",e)})}),s.appendChild(t),o.appendChild(e.parentNode.removeChild(e)),n.appendChild(s),n.appendChild(o),e.parentNode.insertBefore(n,e)})}