document.addEventListener("DOMContentLoaded",e=>{addHeadersToCodeBlocks()});function addHeadersToCodeBlocks(){const e=document.querySelectorAll('pre > code[class^="language-"]');e.forEach(e=>{const n=document.createElement("div");n.classList.add("code-block-wrapper"),e.parentNode.insertBefore(n,e),n.appendChild(e.parentNode.removeChild(e));const s=document.createElement("div");s.classList.add("code-block-header"),s.textContent="Swift",n.appendChild(s);const t=document.createElement("button");t.classList.add("copy-code-button"),t.innerHTML='<i class="far fa-copy"></i>',t.addEventListener("click",()=>{const n=e.innerText;navigator.clipboard.writeText(n).then(()=>{t.innerHTML='<i class="fas fa-check"></i>',setTimeout(()=>{t.innerHTML='<i class="far fa-copy"></i>'},1500)}).catch(e=>{console.error("Failed to copy: ",e)})}),n.appendChild(t)})}